import{_ as t,c as s,o as r,ae as i}from"./chunks/framework.CDjunVez.js";const g=JSON.parse('{"title":"Users & Roles","description":"","frontmatter":{},"headers":[],"relativePath":"docs/guide/users.md","filePath":"docs/guide/users.md"}'),a={name:"docs/guide/users.md"};function o(l,e,n,d,c,u){return r(),s("div",null,[...e[0]||(e[0]=[i('<h1 id="users-roles" tabindex="-1">Users &amp; Roles <a class="header-anchor" href="#users-roles" aria-label="Permalink to &quot;Users &amp; Roles&quot;">​</a></h1><p>Manage users and their permissions in Mist.</p><h2 id="system-roles" tabindex="-1">System Roles <a class="header-anchor" href="#system-roles" aria-label="Permalink to &quot;System Roles&quot;">​</a></h2><p>Mist has a hierarchical role-based access control system with three levels:</p><h3 id="owner" tabindex="-1">Owner <a class="header-anchor" href="#owner" aria-label="Permalink to &quot;Owner&quot;">​</a></h3><p><strong>Highest privilege level</strong> - Complete system control</p><ul><li>Only ONE owner exists per Mist instance</li><li>Created automatically during first-time setup</li><li>Cannot be duplicated through UI or API</li><li>Full access to all features and settings</li><li>Can view and update system settings</li><li>Can create and delete users (including admins)</li><li>Can view all audit logs</li><li>Can delete themselves (resets system to setup mode)</li></ul><h3 id="admin" tabindex="-1">Admin <a class="header-anchor" href="#admin" aria-label="Permalink to &quot;Admin&quot;">​</a></h3><p><strong>High-level administrative privileges</strong> - Second to owner</p><ul><li>Can create and manage projects</li><li>Can create users (admin and user roles)</li><li>Can delete users (except owners and other admins)</li><li>Can view all audit logs</li><li>Access to assigned projects only</li><li><strong>Cannot</strong> view or modify system settings</li><li><strong>Cannot</strong> delete owners or other admins</li><li><strong>Cannot</strong> create owner accounts</li></ul><h3 id="user" tabindex="-1">User <a class="header-anchor" href="#user" aria-label="Permalink to &quot;User&quot;">​</a></h3><p><strong>Standard role</strong> - Basic project access</p><ul><li>Can access assigned projects</li><li>Can deploy applications</li><li>Can manage applications in assigned projects</li><li>Can view logs and metrics</li><li><strong>Cannot</strong> create projects</li><li><strong>Cannot</strong> create other users</li><li><strong>Cannot</strong> view system settings</li><li><strong>Cannot</strong> view audit logs</li></ul><h2 id="role-comparison" tabindex="-1">Role Comparison <a class="header-anchor" href="#role-comparison" aria-label="Permalink to &quot;Role Comparison&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Permission</th><th>Owner</th><th>Admin</th><th>User</th></tr></thead><tbody><tr><td>View system settings</td><td>✅</td><td>❌</td><td>❌</td></tr><tr><td>Update system settings</td><td>✅</td><td>❌</td><td>❌</td></tr><tr><td>Create projects</td><td>✅</td><td>✅</td><td>❌</td></tr><tr><td>Create users</td><td>✅</td><td>✅</td><td>❌</td></tr><tr><td>Delete owners</td><td>✅</td><td>❌</td><td>❌</td></tr><tr><td>Delete admins</td><td>✅</td><td>❌</td><td>❌</td></tr><tr><td>Delete users</td><td>✅</td><td>✅</td><td>❌</td></tr><tr><td>View audit logs</td><td>✅</td><td>✅</td><td>❌</td></tr><tr><td>Access assigned projects</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Manage applications</td><td>✅</td><td>✅</td><td>✅</td></tr></tbody></table><h2 id="managing-users" tabindex="-1">Managing Users <a class="header-anchor" href="#managing-users" aria-label="Permalink to &quot;Managing Users&quot;">​</a></h2><h3 id="creating-users" tabindex="-1">Creating Users <a class="header-anchor" href="#creating-users" aria-label="Permalink to &quot;Creating Users&quot;">​</a></h3><p><strong>Required role</strong>: Owner or Admin</p><ol><li>Navigate to <strong>Settings</strong> → <strong>Users</strong></li><li>Click <strong>&quot;Add User&quot;</strong> or <strong>&quot;New User&quot;</strong></li><li>Fill in user information: <ul><li>Username</li><li>Email address</li><li>Password</li></ul></li><li>Select role: <ul><li><strong>Admin</strong>: For administrative users</li><li><strong>User</strong>: For standard users</li></ul></li><li>Click <strong>&quot;Create&quot;</strong></li></ol><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>You cannot create additional owner accounts. Only one owner exists per Mist instance.</p></div><h3 id="viewing-users" tabindex="-1">Viewing Users <a class="header-anchor" href="#viewing-users" aria-label="Permalink to &quot;Viewing Users&quot;">​</a></h3><p><strong>Required role</strong>: Owner or Admin</p><p>View all users in the system:</p><ul><li>Username</li><li>Email</li><li>Role (displayed with color-coded badges)</li><li>Account creation date</li></ul><p><strong>Role badge colors:</strong></p><ul><li>Owner: Purple</li><li>Admin: Blue</li><li>User: Gray</li></ul><h3 id="updating-users" tabindex="-1">Updating Users <a class="header-anchor" href="#updating-users" aria-label="Permalink to &quot;Updating Users&quot;">​</a></h3><p><strong>Required role</strong>: Owner or Admin</p><p>Modify existing user details:</p><ol><li>Click on the user you want to edit</li><li>Update information: <ul><li>Username</li><li>Email</li><li>Password</li><li>Role (owner/admin only)</li></ul></li><li>Click <strong>&quot;Save&quot;</strong></li></ol><p>All changes are logged in the audit system.</p><h3 id="deleting-users" tabindex="-1">Deleting Users <a class="header-anchor" href="#deleting-users" aria-label="Permalink to &quot;Deleting Users&quot;">​</a></h3><p><strong>Deletion permissions:</strong></p><ul><li><strong>Owner</strong>: Can delete any user (admins and users)</li><li><strong>Admin</strong>: Can only delete users (not owners or other admins)</li><li><strong>User</strong>: Cannot delete anyone</li></ul><p>To delete a user:</p><ol><li>Navigate to the Users page</li><li>Click the delete icon next to the user</li><li>Confirm the deletion</li></ol><div class="warning custom-block"><p class="custom-block-title">Deletion Restrictions</p><ul><li>Admins cannot delete owners or other admins</li><li>Projects and applications created by the deleted user are preserved</li><li>If the owner deletes themselves, the system resets to setup mode</li></ul></div><h2 id="project-roles" tabindex="-1">Project Roles <a class="header-anchor" href="#project-roles" aria-label="Permalink to &quot;Project Roles&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">Coming Soon</p><p>Project-level roles (Owner, Admin, Member) are not yet implemented. Currently, all project members have equal access to project resources.</p><p>See <a href="./projects.html">Projects documentation</a> for more details on project membership.</p></div><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="role-assignment" tabindex="-1">Role Assignment <a class="header-anchor" href="#role-assignment" aria-label="Permalink to &quot;Role Assignment&quot;">​</a></h3><ul><li><strong>Owner</strong>: Reserve for the primary system administrator</li><li><strong>Admin</strong>: For trusted team members who need to manage users and projects</li><li><strong>User</strong>: For team members who only need to work on specific projects</li></ul><h3 id="security-recommendations" tabindex="-1">Security Recommendations <a class="header-anchor" href="#security-recommendations" aria-label="Permalink to &quot;Security Recommendations&quot;">​</a></h3><ol><li><strong>Limit Admins</strong>: Only assign admin role to users who need administrative privileges</li><li><strong>Use Users</strong>: Most team members should have the user role</li><li><strong>Protect Owner</strong>: The owner account should have a strong password and be well-protected</li><li><strong>Regular Audits</strong>: Review user list regularly and remove inactive accounts</li><li><strong>Audit Logs</strong>: Monitor audit logs for suspicious activity</li></ol><h3 id="account-management" tabindex="-1">Account Management <a class="header-anchor" href="#account-management" aria-label="Permalink to &quot;Account Management&quot;">​</a></h3><ul><li>Set strong password requirements for all users</li><li>Remove users who no longer need access</li><li>Review role assignments periodically</li><li>Document who has owner and admin access</li></ul><h2 id="related-documentation" tabindex="-1">Related Documentation <a class="header-anchor" href="#related-documentation" aria-label="Permalink to &quot;Related Documentation&quot;">​</a></h2><ul><li><a href="./authentication.html">Authentication</a> - Detailed authentication information</li><li><a href="./audit-logs.html">Audit Logs</a> - Track user actions</li><li><a href="./projects.html">Projects</a> - Project membership management</li></ul>',48)])])}const h=t(a,[["render",o]]);export{g as __pageData,h as default};
